import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/services/api.service';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.scss']
})
export class MainComponent implements OnInit {
  private API_IMAGES_URL = 'https://storage.googleapis.com/nanlabs-engineering-technical-interviews/imgix-samples-list.json';
  public imagesList: Array<any> = [];
  constructor(private apiService : ApiService){
  }

  ngOnInit(): void {
      this.loadImages();
  }

  loadImages(){
    this.apiService.get(this.API_IMAGES_URL)
    .subscribe((response: any)  => {
      console.log(response);
      this.imagesList = response;
    });
  }
}
