import { AfterViewInit, ChangeDetectorRef, Component, ElementRef, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Subscription, debounceTime, distinct, fromEvent, map, switchMap, tap } from 'rxjs';
import { ImageList } from 'src/app/interfaces/imageList';
import { ApiService } from 'src/app/services/api.service';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.scss']
})
export class MainComponent implements OnInit, AfterViewInit {
  private API_IMAGES_URL = 'https://storage.googleapis.com/nanlabs-engineering-technical-interviews/imgix-samples-list.json';
  public imagesList: ImageList[] = [];
  public imageSelected: string = '';;
  public imageDefault: string = '../../assets/images/image.png';
  public rotateComponentActive : boolean = false;
  public rotateDegreesForm : FormGroup;
  public subscriptions = new Subscription();
  public defaultImageSelectedValue : string ;
  public lastImageState : string ;
  public lastDegrees : number[] = [] ;
  public rangevalue : number = 0 ;

  @ViewChild('rotateInput',{ static: true }) rotateInput! : FormControl;
  @ViewChild('brightness',{ static: true }) brightness! : ElementRef;


  constructor(private apiService : ApiService, private cd: ChangeDetectorRef){
  }

  ngOnInit(): void {
      this.loadImages();
      this.rotateDegreesForm = new FormGroup({
        degrees: new FormControl(0,[
        Validators.required, Validators.min(0), Validators.max(359)]),
      })
  }

  ngAfterViewInit(): void {
    this.rotationChanges();
  }

  valueChanged(e : any){
    fromEvent<Event>(this.brightness.nativeElement, 'input').pipe(
      map((response : Event) => (response.target as HTMLInputElement).value ),
      debounceTime(300),
      distinct(),
      ).subscribe(result => {
        console.log(result);
      });
  }

  rotateImage(rotationQuery: string){
    this.imageSelected = this.defaultImageSelectedValue;
    this.imageSelected = this.imageSelected + rotationQuery;
    this.lastImageState = this.imageSelected;
  }

  undo(){
  }

  redo(){
  }

  loadImages(){
     this.apiService.get(this.API_IMAGES_URL)
    .pipe(
    map((response : ImageList[]) => response)
    ).subscribe((Response: ImageList[]) => this.imagesList = Response);
  }

  onSelected(imageUrl: string){
    this.imageSelected = imageUrl == 'default' ? this.imageDefault : imageUrl;
    this.defaultImageSelectedValue = this.imageSelected;
    this.lastImageState = this.defaultImageSelectedValue;
  }

  rotationChanges(){
    this.subscriptions.add(this.rotateDegreesForm.controls['degrees'].valueChanges.pipe(
      debounceTime(1000),
      distinct(),
      switchMap(async (response) => {
        if(response > 0 && response < 360){
          let rotationQuery = '?rot=' + response;
          this.rotateImage(rotationQuery);
          this.lastDegrees.push(response);
        }
      })     
      ).subscribe(result => {
        console.log(this.imageSelected);
      }));  
  }

}
