import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-rotate',
  templateUrl: './rotate.component.html',
  styleUrls: ['./rotate.component.scss']
})
export class RotateComponent implements OnInit {
  public rotateDegreesForm: FormGroup;
  public submitted = false;


  constructor(private fb: FormBuilder,){

  }

  ngOnInit(): void {
    this.rotateDegreesForm = this.fb.group({
      degrees: [0, Validators.pattern("^[0-9]*$"),
      Validators.required, Validators.min(0), Validators.max(360)],
  })
  }

  get rotateDegreesFormControl() {
    return this.rotateDegreesForm.controls;
  }

  rotate() {
    this.submitted = true;
    if (this.rotateDegreesForm.valid) {
      console.table(this.rotateDegreesForm.value);
    }
  }
}
